package com.lhj.scrollerdemo;import android.support.annotation.NonNull;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import java.util.List;/** * Created by lhj on 18/9/8 */public class NameItemAdapter extends RecyclerView.Adapter {    private List<String> mData;    public NameItemAdapter(List<String> mData){        this.mData = mData;    }    @NonNull    @Override    public RecyclerView.ViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {        View viewName = LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.item_name,viewGroup,false);        return new MyViewHolder(viewName);    }    @Override    public void onBindViewHolder(@NonNull RecyclerView.ViewHolder viewHolder, int i) {        MyViewHolder holder = (MyViewHolder) viewHolder;        holder.ItemView.setText(mData.get(i));    }    @Override    public int getItemCount() {        return mData.size();    }    static class MyViewHolder extends RecyclerView.ViewHolder{        public TextView ItemView;        public MyViewHolder(@NonNull View itemView) {            super(itemView);            ItemView = itemView.findViewById(R.id.tv_name);        }    }}