package com.lhj.scrollerdemo;import android.content.Context;import android.util.AttributeSet;import android.view.MotionEvent;import android.view.ViewConfiguration;import android.widget.ScrollView;/** * Created by lhj on 18/9/8 */public class VerticalScrollerView extends ScrollView {    private int downY;    private int mTouchSlop;    private static SVYOnScrollListener myOnScrollListener;    public VerticalScrollerView(Context context) {        this(context,null);    }    public VerticalScrollerView(Context context, AttributeSet attrs) {        this(context, attrs,-1);    }    public VerticalScrollerView(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);        mTouchSlop = ViewConfiguration.get(context).getScaledTouchSlop();    }    @Override    public boolean onInterceptTouchEvent(MotionEvent ev) {        int action = ev.getAction();        switch (action){            case MotionEvent.ACTION_DOWN:                downY = (int) ev.getRawY();                break;            case MotionEvent.ACTION_MOVE:                int moveY = (int) ev.getRawY();              if(Math.abs(moveY - downY) > mTouchSlop){                  return true;              }            case MotionEvent.ACTION_UP:                break;        }        return super.onInterceptTouchEvent(ev);    }    @Override    protected void onScrollChanged(int l, int t, int oldl, int oldt) {        super.onScrollChanged(l, t, oldl, oldt);        if(myOnScrollListener != null){            myOnScrollListener.onScroll(t);        }    }    public void setSVYOnScrollListener(SVYOnScrollListener listener) {        myOnScrollListener = listener;    }    public  interface SVYOnScrollListener{        void onScroll(int scrolly);    }}